import React from "react";
import { Text as ReactNativeText } from "react-native";
import { FontSizes, FontWeights, Size, Theme } from "../../models/theme";

export type FontColor =
  | typeof Theme["colors"]["font"]
  | typeof Theme["colors"]["secondaryFont"];

export type TextProps = {
  size?: Size;
  weight?: Size;
  color?: FontColor;
} & React.ComponentProps<typeof ReactNativeText>;

export default function Text(props: TextProps) {
  const size = FontSizes[props.size || "md"];
  const weight = FontWeights[props.weight || "md"];
  const color = props.color || Theme.colors.font;

  return (
    <ReactNativeText
      {...props}
      style={{
        color: color,
        fontSize: size,
        fontWeight: weight,
      }}
    >
      {props.children}
    </ReactNativeText>
  );
}
