import React from "react";
import { Text as _Text } from "react-native";
import { FontSizes, FontWeights, Size, Theme } from "../../models/theme";

export type TextProps = {
  size?: Size;
  weight?: Size;
} & React.ComponentProps<typeof _Text>;

export default function Text(props: TextProps) {
  const defaultSize: number = FontSizes[props.size || "md"];
  const defaultWeight: number = FontWeights[props.weight || "md"];
  return (
    <_Text
      {...props}
      style={{
        color: Theme.colors.font,
        fontSize: defaultSize,
        fontWeight: `${defaultWeight}`,
      }}
    >
      {props.children}
    </_Text>
  );
}
