import React from "react";
import { StyleSheet, TextInput, View } from "react-native";
import Text from "../../components/Text";
import { Theme } from "../../models/theme";
import Button from "../../components/Button";
import Card from "../../components/Card";
import { FormField, LoginFields, RegisterFields } from ".";

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    rowGap: Theme.spacing["sm"],
  },
  input: {
    height: 40,
    backgroundColor: Theme.colors.buttonSecondary,
    color: Theme.colors.font,
    borderRadius: Theme.spacing["sm"],
    padding: Theme.spacing["sm"],
    borderWidth: 0,
  },
  form: {
    flex: 1,
    rowGap: Theme.spacing["md"],
    width: "40%",
  },
  formField: {
    flex: 1,
    rowGap: Theme.spacing["sm"],
  },
});

export default function Entry() {
  const [isLogin, setIsLogin] = React.useState(true);
  const fields = isLogin ? LoginFields : RegisterFields;

  return (
    <View style={styles.container}>
      {isLogin ? Login(fields, setIsLogin) : Register(fields)}
    </View>
  );
}

function Login(
  fields: FormField[],
  setIsLogin: React.Dispatch<React.SetStateAction<boolean>>
) {
  return (
    <Card style={styles.form}>
      <Text size="lg">Login</Text>
      {fields.map((field) => (
        <View>
          <Text>{field.label}</Text>
          <TextInput style={styles.input} />
        </View>
      ))}
      <View>
        <Button onPress={() => setIsLogin((prev) => !prev)}>
          <Text>Login</Text>
        </Button>
      </View>
    </Card>
  );
}

function Register(fields: FormField[]) {
  return (
    <Card style={styles.form}>
      <Text size="lg">Register</Text>
      {fields.map((field) => (
        <View>
          <Text>{field.label}</Text>
          <TextInput style={styles.input} />
        </View>
      ))}
      <View>
        <Button>
          <Text>Register</Text>
        </Button>
      </View>
    </Card>
  );
}
