import React, { useState } from "react";
import { TextInput, View } from "react-native";
import Text from "../../components/Text";
import Button from "../../components/Button";
import Card from "../../components/Card";
import { styles } from "./Entry.style";
import { LoginFields, RegisterFields } from "./Entry.model.";
import { Theme } from "../../models/theme";

type Tab = keyof typeof Tabs;
type TabField = typeof Tabs[keyof typeof Tabs]["fields"][number];

const Tabs = {
  Login: {
    title: "Login",
    fields: LoginFields,
  },
  Register: {
    title: "Register",
    fields: RegisterFields,
  },
} as const;

export default function Entry() {
  const [activeTab, setActiveTab] = useState<Tab>(Tabs.Login.title);
  const [formFields, setFormFields] = useState<Record<Tab, TabField[]>>();

  function handleSubmit() {
    const isLogin = activeTab === Tabs.Login.title;
    if (isLogin) {
      const email = formFields?.Login?.find((field) => field.label === "Email");
      const password = formFields?.Login?.find(
        (field) => field.label === "Password"
      );
    } else {
    }
  }

  return (
    <View style={styles.container}>
      <Card style={styles.form}>
        {/* Tabs */}
        <View
          style={{
            ...styles.tabs,
            borderBottomColor: Theme.colors.buttonSecondary,
            paddingBottom: Theme.spacing.md,
            borderBottomWidth: 2,
          }}
        >
          {Object.values(Tabs).map(({ title }) => (
            <Button
              onPress={() => setActiveTab(title)}
              color={
                activeTab === title
                  ? Theme.colors.button
                  : Theme.colors.buttonSecondary
              }
              style={{
                flex: 1,
                borderRadius: 0,
              }}
            >
              <Text>{title}</Text>
            </Button>
          ))}
        </View>

        {/* Fields */}
        <View style={styles.formFieldContainer}>
          {Tabs[activeTab].fields.map(({ label, type }) => (
            <View key={label} style={styles.formField}>
              <Text>{label}</Text>
              <TextInput
                secureTextEntry={type === "password"}
                style={styles.input}
              />
            </View>
          ))}
        </View>

        {/* Submit */}
        <View
          style={{
            borderTopColor: Theme.colors.buttonSecondary,
            paddingTop: Theme.spacing.md,
            borderTopWidth: 2,
          }}
        >
          <Button onPress={() => {}}>
            <Text>Submit</Text>
          </Button>
        </View>
      </Card>
    </View>
  );
}
