// Inspiration:

import { StyleSheet } from "react-native";

// https://cdn.dribbble.com/userupload/6073936/file/original-a06fcadf16954921ab0006bb136244dc.jpg?compress=1&resize=1600x1200
export const Theme = {
  colors: {
    background: "#121723",
    card: "#182336",
    button: "#314468",
    font: "#fefeff",
    secondaryFont: "#5c6471",
    primary: "#3970ff",
  },
} as const;

export const Sizes = ["sm", "md", "lg"] as const;
export type Size = typeof Sizes[number];

export const FontSizes: Record<Size, number> = {
  sm: 12,
  md: 16,
  lg: 20,
};

export const FontWeights: Record<Size, number> = {
  sm: 400,
  md: 500,
  lg: 600,
};

export const GlobalStyles = StyleSheet.create({
  container: {
    backgroundColor: Theme.colors.background,
    color: Theme.colors.font,
    flex: 1,
  },
});
